async function r(n){const t=process.env.AVATAX_COMPANY_CODE;if(!t)throw new Error("Missing AVATAX company code in environment variables.");const e=await fetch("https://exemptioniq-api.onrender.com/exemptioniq-api/auth/get-credentials",{method:"POST",headers:{Authorization:`Bearer ${n.token}`,"Content-Type":"application/json"},body:JSON.stringify({companyCode:t})});if(!e.ok)throw new Error(`Failed to fetch Avalara credentials: ${await e.text()}`);const a=await e.json();return{username:a.avatax_username,password:a.avatax_password,companyId:a.avatax_company_id,clientId:a.avatax_client_id,baseUrl:a.avatax_base_url}}export{r as getAvalaraCredentials};
